import React from "react";
//import components
import StarRating from "./StarRating";

const Course = ({ name, desc, url, key }) => {
  return (
    <div className="card">
      <div>
        <img src={url} alt={name} />
      </div>
      <h2>{name}</h2>
      <p>{desc}</p>
      <h3>Course Rating</h3>
      {/* A self-contained star rating component */}
      <StarRating key={key} />
    </div>
  );
};

export default Course;
